import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';
import { Container, Card, Row, Col, Button } from 'react-bootstrap';

const ClienteHome = () => {
  const [eventos, setEventos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const usuario = JSON.parse(localStorage.getItem('usuario_cliente'));

    if (!usuario || !usuario._id) {
      alert('Você precisa estar logado como cliente.');
      navigate('/login-cliente');
      return;
    }

    api.get('/eventos')
      .then(res => {
        console.log('EVENTOS DA API:', res.data);
        console.log('USUARIO LOGADO:', usuario);

        const eventosFiltrados = res.data.filter(e => {
          const donoDireto = e.dono?.toString() === usuario._id;
          const donoDoPet = e.pet?.dono?.toString() === usuario._id;
          return donoDireto || donoDoPet;
        });

        console.log('EVENTOS FILTRADOS:', eventosFiltrados);
        setEventos(eventosFiltrados);
      })
      .catch(err => {
        console.error('Erro ao buscar eventos:', err);
      });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('usuario_cliente');
    navigate('/');
  };

  return (
    <Container className="mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Meus Eventos</h2>
        <Button variant="secondary" onClick={handleLogout}>
          Sair
        </Button>
      </div>

      <Row>
        {eventos.map((evento) => (
          <Col md={6} lg={4} className="mb-4" key={evento._id}>
            <Card>
              <Card.Body>
                <Card.Title>{evento.tipo}</Card.Title>
                <Card.Text>
                  <strong>Entrada:</strong> {new Date(evento.dataEntrada).toLocaleString()}<br />
                  {evento.dataSaida && (
                    <>
                      <strong>Saída:</strong> {new Date(evento.dataSaida).toLocaleString()}<br />
                    </>
                  )}
                  <strong>Status:</strong> {evento.status}
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default ClienteHome;
