// src/pages/PetList.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';

const PetList = () => {
  const [pets, setPets] = useState([]);

  useEffect(() => {
    api.get('/pets')
      .then(res => setPets(res.data))
      .catch(err => console.error('Erro ao buscar pets', err));
  }, []);

  return (
    <div className="container my-5">
      <h2>Pets Cadastrados</h2>

      {/* Botão de Voltar ao Início */}
      <Link to="/" className="btn btn-secondary mb-3">← Voltar ao Início</Link>

      <div className="list-group">
        {pets.map(pet => (
          <Link
            to={`/pets/${pet._id}`}
            key={pet._id}
            className="list-group-item list-group-item-action"
          >
            <strong>{pet.nome}</strong> – {pet.raca} ({pet.porte})
          </Link>
        ))}
      </div>

      <Link to="/pets/novo" className="btn btn-primary mt-4">+ Cadastrar Novo Pet</Link>
    </div>
  );
};

export default PetList;
