import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';

export default function PetList() {
  const [pets, setPets] = useState([]);

  useEffect(() => {
    api.get('/pets')
      .then(response => setPets(response.data))
      .catch(error => console.error('Erro ao buscar pets:', error));
  }, []);

  return (
    <div style={wrapperStyle}>
      <div style={cardStyle}>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0">üêæ Pets Cadastrados</h2>
          <div>
            <Link to="/" className="btn btn-secondary me-2">‚Üê Voltar ao In√≠cio</Link>
            <Link to="/pets/novo" className="btn btn-primary">+ Cadastrar Novo Pet</Link>
          </div>
        </div>

        {pets.length === 0 ? (
          <p>Nenhum pet cadastrado ainda.</p>
        ) : (
          <ul className="list-group">
            {pets.map(pet => (
              <Link
                to={`/pets/${pet._id}`}
                key={pet._id}
                className="list-group-item list-group-item-action"
                style={{ textDecoration: 'none', color: 'inherit' }}
              >
                <strong>{pet.nome}</strong> ‚Äì {pet.raca} ‚Äì {pet.porte}
              </Link>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

const wrapperStyle = {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  minHeight: '100vh',
  background: 'linear-gradient(to bottom right, #f0f4f8, #ffffff)',
  fontFamily: 'Arial, sans-serif',
  padding: '1rem'
};

const cardStyle = {
  background: '#fff',
  padding: '2rem',
  borderRadius: '16px',
  boxShadow: '0 4px 16px rgba(0, 0, 0, 0.1)',
  textAlign: 'left',
  maxWidth: '700px',
  width: '100%'
};
