// src/pages/PetDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function PetDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [pet, setPet] = useState(null);
  const [eventos, setEventos] = useState([]);
  const [erro, setErro] = useState(null);
  const [editando, setEditando] = useState(false);

  useEffect(() => {
    async function fetchData() {
      try {
        const [petRes, eventosRes] = await Promise.all([
          api.get(`/pets/${id}`),
          api.get(`/eventos/pet/${id}`)
        ]);
        setPet(petRes.data);
        setEventos(eventosRes.data);
      } catch (err) {
        console.error(err);
        setErro('Erro ao carregar dados do pet ou eventos.');
      }
    }
    fetchData();
  }, [id]);

  const handleCheckout = async (eventoId) => {
    try {
      const now = new Date().toISOString();
      const res = await api.put(`/eventos/${eventoId}/checkout`, {
        dataSaida: now,
        observacoes: `Check-out realizado em ${new Date(now).toLocaleString()}`
      });
      setEventos(evts => evts.map(e => (e._id === eventoId ? res.data : e)));
    } catch (err) {
      console.error(err);
      setErro('Erro ao finalizar evento');
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setPet(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/pets/${id}`, pet);
      setEditando(false);
    } catch (err) {
      console.error(err);
      setErro('Erro ao atualizar o pet');
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Tem certeza que deseja excluir este pet?')) {
      await api.delete(`/pets/${id}`);
      navigate('/pets');
    }
  };

  if (!pet) return <div className="container my-5">Carregando pet…</div>;

  return (
    <div className="container my-5">
      <h2>Detalhes do Pet</h2>
      <Link to="/pets" className="btn btn-secondary mb-3">← Voltar</Link>

      {erro && <div className="alert alert-danger">{erro}</div>}

      {editando ? (
        <form onSubmit={handleUpdate}>
          <div className="mb-3">
            <label className="form-label">Nome</label>
            <input name="nome" className="form-control" value={pet.nome} onChange={handleChange} />
          </div>
          <div className="mb-3">
            <label className="form-label">Raça</label>
            <input name="raca" className="form-control" value={pet.raca} onChange={handleChange} />
          </div>
          <div className="mb-3">
            <label className="form-label">Idade</label>
            <input name="idade" className="form-control" value={pet.idade} onChange={handleChange} />
          </div>
          <div className="mb-3">
            <label className="form-label">Peso</label>
            <input name="peso" className="form-control" value={pet.peso} onChange={handleChange} />
          </div>
          <button type="submit" className="btn btn-primary">Salvar</button>
        </form>
      ) : (
        <>
          <p><strong>Nome:</strong> {pet.nome}</p>
          <p><strong>Espécie:</strong> {pet.especie}</p>
          <p><strong>Raça:</strong> {pet.raca}</p>
          <p><strong>Idade:</strong> {pet.idade} anos</p>
          <p><strong>Peso:</strong> {pet.peso} kg</p>
          <p><strong>Porte:</strong> {pet.porte}</p>
          <p><strong>Sexo:</strong> {pet.sexo}</p>
          <p><strong>Cor:</strong> {pet.cor}</p>
          <p><strong>Castrado:</strong> {pet.castrado ? 'Sim' : 'Não'}</p>
          <p><strong>Tutor:</strong> {pet.tutor?.nome}</p>
          <p><strong>Dono responsável:</strong> {pet.dono?.nome}</p>

          <button className="btn btn-warning me-2" onClick={() => setEditando(true)}>Editar</button>
          <button className="btn btn-danger" onClick={handleDelete}>Excluir</button>
        </>
      )}

      <hr />
      <div className="d-flex justify-content-between align-items-center">
        <h3>Histórico de Eventos</h3>
        <Link to={`/pets/${id}/novo-evento`} className="btn btn-success">+ Novo Evento</Link>
      </div>

      <ul className="list-group mt-3">
        {eventos.map(evt => {
          const entrada = new Date(evt.dataEntrada).toLocaleString();
          const saida = evt.dataSaida && new Date(evt.dataSaida).toLocaleString();
          return (
            <li key={evt._id} className="list-group-item d-flex justify-content-between align-items-start">
              <div>
                <strong>{evt.tipo.toUpperCase()}</strong> — {entrada}
                {saida && <> até {saida}</>}
                <br />
                <small>{evt.observacoes}</small>
              </div>
              {!saida && (
                <button
                  className="btn btn-outline-primary btn-sm"
                  onClick={() => handleCheckout(evt._id)}
                >
                  Check-out
                </button>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
}
