import React, { useState } from 'react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [perfil, setPerfil] = useState('admin'); // padrão inicial

  const handleLogin = (e) => {
    e.preventDefault();

    // Simulação de login
    const credenciaisValidas = (
      (perfil === 'admin' && email === 'admin@walky.com' && senha === '123456') ||
      (perfil === 'dono' && email === 'dono@walky.com' && senha === '123456')
    );

    if (credenciaisValidas) {
      const usuario = { nome: 'Usuário', email, perfil };
      localStorage.setItem('usuario', JSON.stringify(usuario));
      window.location.href = perfil === 'dono' ? '/cliente' : '/';
    } else {
      alert('Credenciais inválidas!');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login Walky</h2>
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label>Tipo de Acesso:</label>
          <select className="form-control" value={perfil} onChange={(e) => setPerfil(e.target.value)}>
            <option value="admin">Painel Administrativo</option>
            <option value="dono">Painel do Dono (Cliente)</option>
          </select>
        </div>
        <div className="mb-3">
          <label>Email:</label>
          <input
            className="form-control"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Senha:</label>
          <input
            className="form-control"
            type="password"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            required
          />
        </div>
        <button className="btn btn-primary" type="submit">Entrar</button>
      </form>
    </div>
  );
};

export default Login;
