// src/services/api.js
import axios from 'axios';

const api = axios.create({
  // Se você usou proxy no package.json do frontend:
  baseURL: '/api'
  // Ou, se não usou proxy, descomente a linha abaixo:
  // baseURL: 'http://localhost:5000/api',
});

export default api;
