// src/pages/ClienteHome.jsx
import React, { useEffect, useState } from 'react';
import api from '../services/api';

const ClienteHome = () => {
  const [eventos, setEventos] = useState([]);
  const [carregando, setCarregando] = useState(true);
  const usuario = JSON.parse(localStorage.getItem('usuario'));

  useEffect(() => {
    const buscarEventosDoDono = async () => {
      try {
        const resposta = await api.get('/eventos');
        const eventosDoDono = resposta.data.filter(
          (evento) => evento.pet?.dono?.email === usuario.email
        );
        setEventos(eventosDoDono);
      } catch (erro) {
        console.error('Erro ao buscar eventos do dono:', erro);
      } finally {
        setCarregando(false);
      }
    };

    buscarEventosDoDono();
  }, [usuario.email]);

  const handleLogout = () => {
    localStorage.removeItem('usuario');
    window.location.href = '/login'; // força reset total
  };

  if (carregando) return <p>Carregando...</p>;

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Seus Eventos de Pet</h2>
        <button className="btn btn-danger" onClick={handleLogout}>
          Sair
        </button>
      </div>
      {eventos.length === 0 ? (
        <p>Nenhum evento encontrado para seu pet.</p>
      ) : (
        <ul className="list-group">
          {eventos.map((evento) => (
            <li key={evento._id} className="list-group-item">
              <strong>{evento.tipo}</strong> - {evento.status} - Pet: {evento.pet?.nome}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ClienteHome;
