import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../../services/api';

export default function NovoTutor() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    nome: '', telefone: '', email: '', cpf: '', endereco: '', dataNascimento: ''
  });
  const [errors, setErrors] = useState({});

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setErrors(prev => ({ ...prev, [name]: null }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await api.post('/tutores', form);
      navigate('/tutores');
    } catch (err) {
      const resp = err.response?.data;
      if (resp?.erros) {
        const fieldErrors = resp.erros.reduce((acc, cur) => ({ ...acc, [cur.path]: cur.msg }), {});
        setErrors(fieldErrors);
      } else {
        setErrors({ form: resp?.erro || 'Erro ao cadastrar tutor' });
      }
    }
  };

  return (
    <div className="container my-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Cadastrar Novo Tutor</h2>
        <div>
          <Link to="/" className="btn btn-outline-secondary me-2">Voltar ao In√≠cio</Link>
          <Link to="/tutores" className="btn btn-secondary">Ver Tutores</Link>
        </div>
      </div>

      {errors.form && <div className="alert alert-danger">{errors.form}</div>}

      <form onSubmit={handleSubmit}>
        {['nome', 'telefone', 'email', 'cpf', 'endereco'].map((field) => (
          <div className="mb-3" key={field}>
            <label className="form-label">{field.charAt(0).toUpperCase() + field.slice(1)}</label>
            <input name={field} className="form-control" value={form[field]} onChange={handleChange} />
            {errors[field] && <div className="text-danger">{errors[field]}</div>}
          </div>
        ))}

        <div className="mb-3">
          <label className="form-label">Data de Nascimento</label>
          <input type="date" name="dataNascimento" className="form-control" value={form.dataNascimento} onChange={handleChange} />
          {errors.dataNascimento && <div className="text-danger">{errors.dataNascimento}</div>}
        </div>

        <button type="submit" className="btn btn-primary">Cadastrar Tutor</button>
      </form>
    </div>
  );
}
