import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';

export default function LoginCliente() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState(null);

  // Evita login com usuário admin no lado cliente
  useEffect(() => {
    const admin = JSON.parse(localStorage.getItem('usuario'));
    if (admin?.perfil === 'admin') {
      localStorage.removeItem('usuario');
    }
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await api.post('/donos/login', { email, senha });

      const { _id, nome } = response.data;

      const usuarioCliente = {
        _id,
        nome,
        perfil: 'cliente',
      };

      localStorage.setItem('usuario_cliente', JSON.stringify(usuarioCliente));
      console.log('✅ Cliente logado:', usuarioCliente);

      navigate('/cliente'); // Página inicial do cliente
    } catch (err) {
      console.error('❌ Erro ao logar como cliente:', err.response?.data || err.message);
      setErro('Email ou senha inválidos');
    }
  };

  return (
    <div className="container my-5">
      <div className="p-4 border rounded shadow-sm bg-white col-md-6 offset-md-3">
        <h2 className="mb-4">Login do Cliente</h2>

        {erro && <div className="alert alert-danger">{erro}</div>}

        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <label className="form-label">Email</label>
            <input
              type="email"
              className="form-control"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Senha</label>
            <input
              type="password"
              className="form-control"
              value={senha}
              onChange={e => setSenha(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary w-100">Entrar</button>
        </form>
      </div>
    </div>
  );
}
