// src/pages/Evento/NovoEvento.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../../services/api';
import { Container, Form, Button, Alert } from 'react-bootstrap';

export default function NovoEvento() {
  const { petId } = useParams();
  const navigate = useNavigate();

  const [pet, setPet] = useState(null);
  const [tipo, setTipo] = useState('Hospedagem');
  const [dataEntrada, setDataEntrada] = useState('');
  const [erro, setErro] = useState('');

  // 1. Carrega dados do pet
  useEffect(() => {
    if (!petId) {
      setErro('ID do pet não fornecido na URL.');
      return;
    }
    (async () => {
      try {
        const { data } = await api.get(`/pets/${petId}`);
        setPet(data);
      } catch {
        setErro('Falha ao carregar dados do pet.');
      }
    })();
  }, [petId]);

  // 2. Envio do formulário
  const handleSubmit = async e => {
    e.preventDefault();
    setErro('');

    if (!dataEntrada) {
      setErro('Informe a data de entrada.');
      return;
    }

    try {
      await api.post('/eventos', {
        tipo,
        pet: petId,
        tutor: pet.tutor,
        dono: pet.dono,
        dataEntrada
      });
      navigate(`/pets/${petId}`);
    } catch (err) {
      setErro(err.response?.data?.erro || 'Erro ao salvar evento.');
    }
  };

  if (erro) return <Container className="mt-4"><Alert variant="danger">{erro}</Alert></Container>;
  if (!pet) return <Container className="mt-4"><p>Carregando pet...</p></Container>;

  return (
    <Container className="mt-4">
      <h2>Cadastrar Evento para <strong>{pet.nome}</strong></h2>
      <Form onSubmit={handleSubmit} className="mt-3">
        <Form.Group className="mb-3">
          <Form.Label>Tipo de Evento</Form.Label>
          <Form.Select value={tipo} onChange={e => setTipo(e.target.value)}>
            {['Hospedagem','Passeio','Banho','Consulta','Alimentacao','Recreacao']
              .map(o => <option key={o} value={o}>{o}</option>)
            }
          </Form.Select>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Data de Entrada</Form.Label>
          <Form.Control
            type="datetime-local"
            value={dataEntrada}
            onChange={e => setDataEntrada(e.target.value)}
            required
          />
        </Form.Group>

        <div className="d-flex">
          <Button variant="success" type="submit">Salvar</Button>
          <Button variant="secondary" className="ms-2" onClick={() => navigate(`/pets/${petId}`)}>
            Cancelar
          </Button>
        </div>
      </Form>
    </Container>
  );
}
