// src/pages/NovoEvento.jsx
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

const NovoEvento = () => {
  const { petId } = useParams();
  const navigate = useNavigate();

  const [tipo, setTipo] = useState('Hospedagem');
  const [dataEntrada, setDataEntrada] = useState('');
  const [observacoes, setObservacoes] = useState('');
  const [pet, setPet] = useState(null);

  useEffect(() => {
    const carregarPet = async () => {
      try {
        const response = await api.get(`/pets/${petId}`);
        setPet(response.data);
      } catch (error) {
        console.error('Erro ao carregar pet:', error);
      }
    };

    carregarPet();
  }, [petId]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const novoEvento = {
      tipo,
      pet: petId,
      dataEntrada,
      observacoes,
      status: "Ativo",
      tutor: pet?.tutor?._id || undefined,
      dono: pet?.dono?._id || undefined,
      admin: pet?.criador?._id || undefined
    };

    try {
      await api.post("/eventos", novoEvento);
      alert("Evento cadastrado com sucesso!");
      navigate(`/pets/${petId}`);
    } catch (error) {
      console.error("Erro ao cadastrar evento:", error);
      alert("Erro ao cadastrar evento.");
    }
  };

  if (!pet) {
    return <div>Carregando pet...</div>;
  }

  return (
    <div className="container mt-5 p-4 bg-white rounded shadow">
      <h2 className="mb-4">Cadastrar Evento para {pet.nome}</h2>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Tipo de Evento:</label>
          <select className="form-select" value={tipo} onChange={(e) => setTipo(e.target.value)}>
            <option value="Hospedagem">Hospedagem</option>
            <option value="Passeio">Passeio</option>
            <option value="Banho">Banho</option>
            <option value="Consulta">Consulta</option>
            <option value="Alimentacao">Alimentação</option>
            <option value="Recreacao">Recreação</option>
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Data de Entrada:</label>
          <input
            type="datetime-local"
            className="form-control"
            value={dataEntrada}
            onChange={(e) => setDataEntrada(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Observações:</label>
          <textarea
            className="form-control"
            value={observacoes}
            onChange={(e) => setObservacoes(e.target.value)}
          />
        </div>

        <button type="submit" className="btn btn-success me-2">Salvar Evento</button>
        <Link to={`/pets/${petId}`} className="btn btn-secondary">Cancelar</Link>
      </form>
    </div>
  );
};

export default NovoEvento;
