// src/pages/Tutor/TutorHome.jsx
import React, { useEffect, useState } from 'react';
import { Card, ListGroup, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';

const formatDateTime = iso =>
  new Date(iso).toLocaleString('pt-BR', {
    day:   '2-digit',
    month: '2-digit',
    year:  'numeric',
    hour:  '2-digit',
    minute:'2-digit',
    second:'2-digit',
  });

export default function TutorHome() {
  const navigate = useNavigate();
  const [pets, setPets] = useState([]);
  const [selectedPet, setSelectedPet] = useState(null); // null = “Todos Pets”
  const [eventos, setEventos] = useState([]);

  // 1) Carrega tutor, pets e eventos iniciais
  useEffect(() => {
    const tutorJson = localStorage.getItem('usuario_tutor');
    if (!tutorJson) {
      navigate('/login-tutor');
      return;
    }
    const tutor = JSON.parse(tutorJson);
    api.defaults.headers.common['Authorization'] = `Bearer ${tutor.token}`;

    api.get(`/pets?tutor=${tutor._id}`)
      .then(({ data }) => setPets(data))
      .catch(console.error);

    api.get(`/eventos/tutor/${tutor._id}`)
      .then(({ data }) => setEventos(data))
      .catch(console.error);
  }, [navigate]);

  // 2) Recarrega eventos ao mudar de pet
  useEffect(() => {
    const tutor = JSON.parse(localStorage.getItem('usuario_tutor'));
    const url =
      selectedPet === null
        ? `/eventos/tutor/${tutor._id}`
        : `/eventos/pet/${selectedPet}`;

    api.get(url)
      .then(({ data }) => setEventos(data))
      .catch(console.error);
  }, [selectedPet]);

  // 3) Check-in (abre o form de novo evento)
  const handleCheckIn = () => {
    if (!selectedPet) return;
    navigate(`/pets/${selectedPet}/novo-evento`);
  };

  // 4) Check-out (fecha o evento em andamento)
  const handleCheckOut = id => {
    api.put(`/eventos/${id}/checkout`, { dataSaida: new Date().toISOString() })
      .then(({ data }) => {
        setEventos(evts =>
          evts.map(evt =>
            evt._id === id
              ? { ...evt, dataSaida: data.dataSaida, status: data.status }
              : evt
          )
        );
      })
      .catch(() => alert('Falha ao executar check-out'));
  };

  // 5) Excluir evento
  const handleDeleteEvent = async id => {
    if (!window.confirm('Deseja realmente excluir este evento?')) return;
    try {
      await api.delete(`/eventos/${id}`);
      setEventos(evts => evts.filter(evt => evt._id !== id));
    } catch {
      alert('Falha ao excluir evento.');
    }
  };

  return (
    <div className="container my-4">
      {/* Header */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Painel do Tutor</h2>
        <Button variant="secondary" onClick={() => navigate('/')}>
          Sair
        </Button>
      </div>

      <div className="row">
        {/* Lista de Pets */}
        <div className="col-md-4">
          <Card className="mb-4 shadow-sm">
            <Card.Header className="bg-white border-0">
              <h5 className="mb-0">Meus Pets</h5>
            </Card.Header>
            <ListGroup variant="flush">
              <ListGroup.Item
                action
                active={selectedPet === null}
                onClick={() => setSelectedPet(null)}
              >
                Todos Pets
              </ListGroup.Item>
              {pets.map(p => (
                <ListGroup.Item
                  key={p._id}
                  action
                  active={p._id === selectedPet}
                  onClick={() => setSelectedPet(p._id)}
                >
                  {p.nome}
                </ListGroup.Item>
              ))}
            </ListGroup>
          </Card>
        </div>

        {/* Lista de Eventos */}
        <div className="col-md-8">
          <Card className="shadow-sm">
            <Card.Header className="bg-white border-0 d-flex justify-content-between align-items-center">
              <h5 className="mb-0">Meus Eventos</h5>
              <Button
                variant="success"
                size="sm"
                onClick={handleCheckIn}
                disabled={!selectedPet}
              >
                Check-in
              </Button>
            </Card.Header>

            <ListGroup variant="flush">
              {eventos.length === 0 && (
                <ListGroup.Item>Nenhum evento registrado.</ListGroup.Item>
              )}

              {eventos.map(evt => (
                <ListGroup.Item
                  key={evt._id}
                  className="d-flex justify-content-between align-items-start"
                >
                  <div className="w-100">
                    <div>
                      <strong>{evt.tipo}</strong> —<br />
                      Entrada: {formatDateTime(evt.dataEntrada)}<br />
                      Saída:{' '}
                      {evt.dataSaida
                        ? formatDateTime(evt.dataSaida)
                        : '—'}<br />
                      Status: {evt.status}
                    </div>

                    {evt.observacoes && (
                      <div className="mt-2 ps-2 border-start">
                        <em>Obs:</em> {evt.observacoes}
                      </div>
                    )}
                  </div>

                  <div className="d-flex flex-column align-items-end">
                    {!evt.dataSaida && (
                      <Button
                        variant="warning"
                        size="sm"
                        onClick={() => handleCheckOut(evt._id)}
                        className="mb-2"
                      >
                        Check-out
                      </Button>
                    )}
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => handleDeleteEvent(evt._id)}
                    >
                      Excluir
                    </Button>
                  </div>
                </ListGroup.Item>
              ))}
            </ListGroup>
          </Card>
        </div>
      </div>
    </div>
  );
}
