// src/pages/Cliente/ClienteHome.jsx
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../../services/api';

export default function ClienteHome() {
  const navigate = useNavigate();
  const [dono, setDono] = useState(null);
  const [meusPets, setMeusPets] = useState([]);       // agora guarda objetos de Pet
  const [meusEventos, setMeusEventos] = useState([]);

  const donoStr = localStorage.getItem('usuario_cliente');
  const usuario = donoStr ? JSON.parse(donoStr) : null;

  useEffect(() => {
    if (!usuario?._id) {
      alert('VocÃª precisa estar logado como cliente.');
      navigate('/login-cliente');
      return;
    }

    async function loadClienteData() {
      try {
        // busca dados do dono e seus pets
        const resDono = await api.get(`/donos/${usuario._id}`);
        const donoData = resDono.data;
        setDono(donoData);

        // busca detalhes de cada pet
        const petObjs = await Promise.all(
          (donoData.pets || []).map(idPet =>
            api.get(`/pets/${idPet}`).then(r => r.data)
          )
        );
        setMeusPets(petObjs);

        // busca eventos de cada pet e anexa petName
        const eventosPorPet = await Promise.all(
          petObjs.map(pet =>
            api.get(`/eventos/pet/${pet._id}`).then(res =>
              res.data.map(evt => ({ ...evt, petName: pet.nome }))
            )
          )
        );
        // achata e ordena
        const allEventos = eventosPorPet
          .flat()
          .sort((a, b) => new Date(b.dataEntrada) - new Date(a.dataEntrada));
        setMeusEventos(allEventos);
      } catch (err) {
        console.error(err);
        alert('Erro ao carregar dados.');
        navigate('/login-cliente');
      }
    }

    loadClienteData();
  }, [usuario, navigate]);

  if (!dono) return <p>Carregando perfil...</p>;

  const formatDateTime = iso => new Date(iso).toLocaleString('pt-BR');

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Painel do Cliente</h2>
        <button
          className="btn btn-secondary"
          onClick={() => {
            localStorage.removeItem('usuario_cliente');
            navigate('/login-cliente');
          }}
        >
          Sair
        </button>
      </div>

      <div className="row">
        {/* Perfil do Dono e lista de Pets */}
        <div className="col-md-4">
          <div className="card mb-4">
            <div className="card-header bg-white border-0">
              <h5 className="mb-0">Meus Dados</h5>
            </div>
            <ul className="list-group list-group-flush">
              <li className="list-group-item"><strong>Nome:</strong> {dono.nome}</li>
              <li className="list-group-item"><strong>E-mail:</strong> {dono.email}</li>
              <li className="list-group-item"><strong>CPF:</strong> {dono.cpf}</li>
              <li className="list-group-item"><strong>Telefone:</strong> {dono.telefone}</li>
              <li className="list-group-item"><strong>EndereÃ§o:</strong> {dono.endereco}</li>
            </ul>
          </div>

          <div className="card">
            <div className="card-header bg-white border-0">
              <h5 className="mb-0">Meus Pets</h5>
            </div>
            <ul className="list-group list-group-flush">
              {meusPets.length === 0 && (
                <li className="list-group-item">Nenhum pet cadastrado.</li>
              )}
              {meusPets.map(pet => (
                <li key={pet._id} className="list-group-item">
                  <Link to={`/pets/${pet._id}`} className="text-decoration-none">
                    {pet.nome}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* Eventos relacionados */}
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-white border-0">
              <h5 className="mb-0">Meus Eventos</h5>
            </div>
            <ul className="list-group list-group-flush">
              {meusEventos.length === 0 && (
                <li className="list-group-item">Nenhum evento registrado.</li>
              )}
              {meusEventos.map(evt => (
                <li key={evt._id} className="list-group-item mb-3">
                  <div className="d-flex justify-content-between">
                    <div>
                      <strong>{evt.tipo}</strong> <br />
                      <small><strong>Pet:</strong> {evt.petName}</small>
                    </div>
                    <div>
                      <strong>Entrada:</strong> {formatDateTime(evt.dataEntrada)}<br/>
                      <strong>Status:</strong> {evt.status || (evt.dataSaida ? 'ConcluÃ­do' : 'Ativo')}
                    </div>
                  </div>
                  {evt.observacoes && (
                    <div className="mt-2">
                      <em>ObservaÃ§Ãµes:</em> {evt.observacoes}
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
