import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../../services/api';

export default function EditarAdmin() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    nome: '',
    email: '',
    senha: '',
    nivelAcesso: 'admin',
    ativo: true
  });

  useEffect(() => {
    api.get(`/admins/${id}`).then(res => setForm(res.data));
  }, [id]);

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    await api.put(`/admins/${id}`, form);
    navigate('/admins');
  };

  return (
    <div className="container my-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Editar Admin</h2>
        <div>
          <Link to="/" className="btn btn-outline-secondary me-2">Voltar ao Início</Link>
          <Link to="/admins" className="btn btn-secondary">Ver Admins</Link>
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Nome</label>
          <input name="nome" className="form-control" value={form.nome} onChange={handleChange} />
        </div>

        <div className="mb-3">
          <label className="form-label">Email</label>
          <input name="email" type="email" className="form-control" value={form.email} onChange={handleChange} />
        </div>

        <div className="mb-3">
          <label className="form-label">Senha</label>
          <input name="senha" type="password" className="form-control" value={form.senha} onChange={handleChange} />
        </div>

        <div className="mb-3">
          <label className="form-label">Nível de Acesso</label>
          <select name="nivelAcesso" className="form-select" value={form.nivelAcesso} onChange={handleChange}>
            <option value="super">Super</option>
            <option value="admin">Admin</option>
            <option value="moderador">Moderador</option>
          </select>
        </div>

        <div className="form-check mb-3">
          <input type="checkbox" className="form-check-input" name="ativo" checked={form.ativo} onChange={handleChange} />
          <label className="form-check-label">Ativo</label>
        </div>

        <button type="submit" className="btn btn-primary">Salvar</button>
      </form>
    </div>
  );
}
