// src/pages/Evento/NovoEvento.jsx

import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../../services/api';

export default function NovoEvento() {
  const { id: petId } = useParams();
  const navigate = useNavigate();
  const [tipo, setTipo] = useState('Hospedagem');
  const [observacoes, setObservacoes] = useState('');
  const [dataEntrada, setDataEntrada] = useState(() => {
    // inicializa com data e hora atuais no formato datetime-local
    const now = new Date();
    return now.toISOString().slice(0, 16);
  });
  const [petNome, setPetNome] = useState('');

  useEffect(() => {
    // carregar nome do pet
    async function fetchPet() {
      try {
        const res = await api.get(`/pets/${petId}`);
        setPetNome(res.data.nome);
      } catch (err) {
        console.error('Erro ao buscar pet:', err);
      }
    }
    fetchPet();
  }, [petId]);

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      await api.post('/eventos', {
        tipo,
        pet: petId,
        tutor: res => res.data.tutor,   // ajuste conforme seu contexto de auth
        admin: res => res.data.criador, // idem
        dataEntrada: new Date(dataEntrada).toISOString(),
        observacoes,
      });
      navigate(`/pets/${petId}`);
    } catch (err) {
      console.error('Erro ao criar evento:', err);
      alert('Falha ao cadastrar evento. Veja o console.');
    }
  }

  return (
    <div className="container mt-4">
      <h2>Novo Evento para: {petNome}</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Tipo de Evento:</label>
          <select
            className="form-select"
            value={tipo}
            onChange={(e) => setTipo(e.target.value)}
          >
            <option>Hospedagem</option>
            <option>Passeio</option>
            <option>Banho</option>
            <option>Consulta</option>
            <option>Alimentacao</option>
            <option>Recreacao</option>
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Data e Hora de Entrada:</label>
          <input
            type="datetime-local"
            className="form-control"
            value={dataEntrada}
            onChange={(e) => setDataEntrada(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Observações:</label>
          <textarea
            className="form-control"
            rows="3"
            value={observacoes}
            onChange={(e) => setObservacoes(e.target.value)}
          />
        </div>

        <button type="submit" className="btn btn-success">
          Salvar Evento
        </button>
      </form>
    </div>
  );
}
