import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';
import { Container, Form, Button, Alert } from 'react-bootstrap';

const LoginAdmin = () => {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setErro('');

    try {
      const res = await api.post('/admins/login', { email, senha });
      localStorage.setItem('usuario_admin', JSON.stringify(res.data));
      navigate('/admin/home');
    } catch (err) {
      const msg = err.response?.data?.message || 'Erro ao conectar com o servidor.';
      setErro(msg);
    }
  };

  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Login Administrador</h2>
      <Form
        onSubmit={handleLogin}
        className="p-4 rounded shadow bg-white mx-auto"
        style={{ maxWidth: '400px' }}
      >
        {erro && <Alert variant="danger">{erro}</Alert>}

        <Form.Group className="mb-3" controlId="emailAdmin">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="Digite seu email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="senhaAdmin">
          <Form.Label>Senha</Form.Label>
          <Form.Control
            type="password"
            placeholder="Digite sua senha"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            required
          />
        </Form.Group>

        <div className="d-flex justify-content-between">
          <Button variant="primary" type="submit">
            Entrar
          </Button>
          <Button variant="secondary" onClick={() => navigate('/') }>
            Voltar
          </Button>
        </div>
      </Form>
    </Container>
  );
};

export default LoginAdmin;
