import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function ClienteHome() {
  const [pets, setPets] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const usuario = JSON.parse(localStorage.getItem('usuario_cliente'));

    if (!usuario || !usuario._id) {
      alert('Você precisa estar logado como cliente.');
      navigate('/login-cliente');
      return;
    }

    // Buscar todos os pets e filtrar os do dono logado
    api.get('/pets')
      .then(res => {
        const todosPets = res.data;
        const petsDoDono = todosPets.filter(p => p.dono === usuario._id);
        setPets(petsDoDono);
      })
      .catch(err => {
        console.error('Erro ao buscar pets:', err);
      });
  }, [navigate]);

  return (
    <div className="container my-5">
      <div className="p-4 border rounded shadow-sm bg-white">
        <h2 className="mb-4">Bem-vindo ao Portal do Cliente</h2>

        {pets.length === 0 ? (
          <p>Nenhum pet encontrado para sua conta.</p>
        ) : (
          <>
            <h4>Seus Pets:</h4>
            <ul className="list-group">
              {pets.map(pet => (
                <li key={pet._id} className="list-group-item d-flex justify-content-between align-items-center">
                  <Link to={`/pets/${pet._id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                    {pet.nome}
                  </Link>
                </li>
              ))}
            </ul>
          </>
        )}
      </div>
    </div>
  );
}
