import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../../services/api';

export default function EditarPet() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nome: '', raca: '', idade: '', peso: '', porte: '',
    sexo: '', cor: '', castrado: false, carteiraVacinacao: false,
    observacoes: '', tutor: '', dono: '', criador: ''
  });

  const [tutores, setTutores] = useState([]);
  const [donos, setDonos] = useState([]);
  const [admins, setAdmins] = useState([]);

  useEffect(() => {
    api.get(`/pets/${id}`)
      .then((response) => {
        const pet = response.data;
        setFormData({
          ...pet,
          tutor: pet.tutor?._id || '',
          dono: pet.dono?._id || '',
          criador: pet.criador?._id || ''
        });
      })
      .catch(err => console.error('Erro ao carregar pet:', err));

    api.get('/tutores').then(res => setTutores(res.data));
    api.get('/donos').then(res => setDonos(res.data));
    api.get('/admins').then(res => setAdmins(res.data));
  }, [id]);

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;
    setFormData({ ...formData, [name]: val });
  };

  const handleSubmit = e => {
    e.preventDefault();
    api.put(`/pets/${id}`, formData)
      .then(() => navigate('/pets'))
      .catch(err => console.error('Erro ao salvar alterações:', err));
  };

  return (
    <div className="container my-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Editar Pet</h2>
        <div>
          <Link to="/" className="btn btn-outline-secondary me-2">Voltar ao Início</Link>
          <Link to="/pets" className="btn btn-secondary">Ver Pets</Link>
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Nome</label>
          <input name="nome" className="form-control" value={formData.nome} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Raça</label>
          <input name="raca" className="form-control" value={formData.raca} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Idade</label>
          <input name="idade" type="number" className="form-control" value={formData.idade} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Peso (kg)</label>
          <input name="peso" type="number" className="form-control" value={formData.peso} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Porte</label>
          <input name="porte" className="form-control" value={formData.porte} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Sexo</label>
          <select className="form-select" name="sexo" value={formData.sexo} onChange={handleChange}>
            <option value="">Selecione</option>
            <option value="Macho">Macho</option>
            <option value="Femea">Fêmea</option>
          </select>
        </div>
        <div className="mb-3">
          <label className="form-label">Cor</label>
          <input name="cor" className="form-control" value={formData.cor} onChange={handleChange} />
        </div>
        <div className="form-check mb-3">
          <input type="checkbox" className="form-check-input" name="castrado" checked={formData.castrado} onChange={handleChange} />
          <label className="form-check-label">Castrado</label>
        </div>
        <div className="form-check mb-3">
          <input type="checkbox" className="form-check-input" name="carteiraVacinacao" checked={formData.carteiraVacinacao} onChange={handleChange} />
          <label className="form-check-label">Carteira de Vacinação em Dia</label>
        </div>
        <div className="mb-3">
          <label className="form-label">Observações</label>
          <textarea name="observacoes" className="form-control" rows="3" value={formData.observacoes} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Tutor</label>
          <select className="form-select" name="tutor" value={formData.tutor} onChange={handleChange}>
            <option value="">Selecione um tutor</option>
            {tutores.map(t => <option key={t._id} value={t._id}>{t.nome}</option>)}
          </select>
        </div>
        <div className="mb-3">
          <label className="form-label">Dono</label>
          <input type="text" className="form-control bg-light" value={donos.find(d => d._id === formData.dono)?.nome || ''} readOnly />
        </div>
        <div className="mb-3">
          <label className="form-label">Criador (Admin)</label>
          <input type="text" className="form-control bg-light" value={admins.find(a => a._id === formData.criador)?.nome || ''} readOnly />
        </div>
        <button type="submit" className="btn btn-primary">Salvar</button>
      </form>
    </div>
  );
}
