import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import api from '../../services/api';

export default function AdminDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [admin, setAdmin] = useState(null);

  useEffect(() => {
    api.get(`/admins/${id}`)
      .then(res => setAdmin(res.data))
      .catch(err => console.error('Erro ao buscar admin:', err));
  }, [id]);

  const handleDelete = async () => {
    if (window.confirm('Tem certeza que deseja excluir este admin?')) {
      try {
        await api.delete(`/admins/${id}`);
        navigate('/admins');
      } catch (err) {
        alert('Erro ao excluir admin.');
      }
    }
  };

  if (!admin) return <p>Carregando...</p>;

  return (
    <div className="container my-5">
      <div className="p-4 border rounded shadow-sm bg-white">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2>Detalhes do Admin</h2>
          <div>
            <Link to="/" className="btn btn-secondary me-2">← Voltar ao Início</Link>
            <Link to="/admins" className="btn btn-dark">Ver Admins</Link>
          </div>
        </div>

        <div className="mb-3"><strong>Nome:</strong> {admin.nome}</div>
        <div className="mb-3"><strong>Email:</strong> {admin.email}</div>
        <div className="mb-3"><strong>Nível de Acesso:</strong> {admin.nivelAcesso}</div>
        <div className="mb-3"><strong>Ativo:</strong> {admin.ativo ? 'Sim' : 'Não'}</div>
        <div className="mb-3"><strong>Data de Cadastro:</strong> {new Date(admin.dataCadastro).toLocaleDateString()}</div>

        <div className="mt-3">
          <Link to={`/admins/${id}/editar`} className="btn btn-warning me-2">Editar</Link>
          <button onClick={handleDelete} className="btn btn-danger">Excluir</button>
        </div>
      </div>
    </div>
  );
}
