// src/pages/PetDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';

const PetDetails = () => {
  const { id } = useParams();
  const [pet, setPet] = useState(null);
  const [eventos, setEventos] = useState([]);
  const [erro, setErro] = useState(null);

  const fetchData = () => {
    api.get(`/pets/${id}`)
      .then(res => setPet(res.data))
      .catch(err => console.error(err));

    api.get(`/pets/${id}/eventos`)
      .then(res => setEventos(res.data))
      .catch(err => console.error(err));
  };

  useEffect(fetchData, [id]);

  const handleCheckout = async (eventoId) => {
    try {
      const now = new Date().toISOString();
      const res = await api.put(`/eventos/${eventoId}/checkout`, {
        dataSaida: now,
        observacoes: 'Check-out realizado pelo tutor em ' + new Date(now).toLocaleString()
      });
      setEventos(evts => evts.map(e => e._id === eventoId ? res.data : e));
    } catch (err) {
      console.error(err);
      setErro('Erro ao finalizar evento');
    }
  };

  if (!pet) {
    return <div className="container my-5">Carregando pet…</div>;
  }

  return (
    <div className="container my-5">
      <h2>{pet.nome} – {pet.raca}</h2>
      <p><strong>Espécie:</strong> {pet.especie}</p>
      <p><strong>Porte:</strong> {pet.porte}</p>
      <p><strong>Tutor:</strong> {pet.tutor?.nome}</p>
      <p><strong>Dono responsável:</strong> {pet.dono?.nome}</p>
      <hr />

      {erro && <div className="alert alert-danger">{erro}</div>}

      <div className="d-flex justify-content-between align-items-center">
        <h3>Histórico de Eventos</h3>
        <Link to={`/pets/${id}/novo-evento`} className="btn btn-success">
          + Novo Evento
        </Link>
      </div>

      <ul className="list-group mt-3">
        {eventos.map(evt => {
          const entrada = new Date(evt.dataEntrada).toLocaleString();
          const saida = evt.dataSaida
            ? new Date(evt.dataSaida).toLocaleString()
            : null;

          return (
            <li key={evt._id} className="list-group-item d-flex justify-content-between align-items-start">
              <div>
                <strong>{evt.tipo.toUpperCase()}</strong> — {entrada}
                {saida && <> até {saida}</>}
                <br/>
                <small>{evt.observacoes}</small>
              </div>
              {!saida && (
                <button
                  className="btn btn-outline-primary btn-sm"
                  onClick={() => handleCheckout(evt._id)}
                >
                  Check-out
                </button>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default PetDetails;
