// src/pages/NovoDono.jsx
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../../services/api';

export default function NovoDono() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nome: '',
    cpf: '',
    endereco: '',
    telefone: '',
    email: '',
    dataNascimento: '',
    responsavel: false,
    ativo: true,
    observacoes: ''
  });

  const [formErrors, setFormErrors] = useState({});

  const formatarCPF = (valor) => {
    return valor
      .replace(/\D/g, '')
      .replace(/(\d{3})(\d)/, '$1.$2')
      .replace(/(\d{3})(\d)/, '$1.$2')
      .replace(/(\d{3})(\d{1,2})$/, '$1-$2');
  };

  const formatarTelefone = (valor) => {
    return valor
      .replace(/\D/g, '')
      .replace(/(\d{2})(\d)/, '($1) $2')
      .replace(/(\d{5})(\d)/, '$1-$2')
      .replace(/(-\d{4})\d+?$/, '$1');
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let inputValue = value;

    if (name === 'cpf') {
      inputValue = formatarCPF(value);
    } else if (name === 'telefone') {
      inputValue = formatarTelefone(value);
    }

    setFormData({ ...formData, [name]: type === 'checkbox' ? checked : inputValue });
  };

  const validate = () => {
    const errors = {};
    const cpfNumerico = formData.cpf.replace(/\D/g, '');
    const telNumerico = formData.telefone.replace(/\D/g, '');

    if (!formData.nome.trim()) errors.nome = 'Nome é obrigatório';
    if (!cpfNumerico || cpfNumerico.length !== 11) errors.cpf = 'CPF inválido (11 dígitos)';
    if (!formData.endereco.trim()) errors.endereco = 'Endereço é obrigatório';
    if (!telNumerico || telNumerico.length < 10 || telNumerico.length > 11) {
      errors.telefone = 'Telefone inválido (use DDD + número)';
    }
    if (!formData.email.trim() || !formData.email.includes('@')) errors.email = 'Email inválido';
    if (!formData.dataNascimento) errors.dataNascimento = 'Data de nascimento é obrigatória';
    return errors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const errors = validate();
    setFormErrors(errors);
    if (Object.keys(errors).length === 0) {
      api.post('/donos', formData)
        .then(() => navigate('/donos'))
        .catch(err => console.error('Erro ao cadastrar dono:', err));
    }
  };

  return (
    <div className="container">
      <div className="d-flex justify-content-end mb-3">
        <Link to="/" className="btn btn-secondary me-2">← Voltar ao Início</Link>
        <Link to="/donos" className="btn btn-primary">Ver Donos</Link>
      </div>
      <h2>Novo Dono</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome:</label>
          <input type="text" name="nome" className="form-control" value={formData.nome} onChange={handleChange} />
          {formErrors.nome && <small className="text-danger">{formErrors.nome}</small>}
        </div>
        <div className="form-group">
          <label>CPF:</label>
          <input type="text" name="cpf" className="form-control" value={formData.cpf} onChange={handleChange} />
          {formErrors.cpf && <small className="text-danger">{formErrors.cpf}</small>}
        </div>
        <div className="form-group">
          <label>Endereço:</label>
          <input type="text" name="endereco" className="form-control" value={formData.endereco} onChange={handleChange} />
          {formErrors.endereco && <small className="text-danger">{formErrors.endereco}</small>}
        </div>
        <div className="form-group">
          <label>Telefone:</label>
          <input type="text" name="telefone" className="form-control" value={formData.telefone} onChange={handleChange} />
          {formErrors.telefone && <small className="text-danger">{formErrors.telefone}</small>}
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" name="email" className="form-control" value={formData.email} onChange={handleChange} />
          {formErrors.email && <small className="text-danger">{formErrors.email}</small>}
        </div>
        <div className="form-group">
          <label>Data de Nascimento:</label>
          <input type="date" name="dataNascimento" className="form-control" value={formData.dataNascimento} onChange={handleChange} />
          {formErrors.dataNascimento && <small className="text-danger">{formErrors.dataNascimento}</small>}
        </div>
        <div className="form-check my-2">
          <input type="checkbox" name="responsavel" className="form-check-input" checked={formData.responsavel} onChange={handleChange} />
          <label className="form-check-label">Responsável</label>
        </div>
        <div className="form-check mb-3">
          <input type="checkbox" name="ativo" className="form-check-input" checked={formData.ativo} onChange={handleChange} />
          <label className="form-check-label">Ativo</label>
        </div>
        <div className="form-group">
          <label>Observações:</label>
          <textarea name="observacoes" className="form-control" value={formData.observacoes} onChange={handleChange} />
        </div>
        <button type="submit" className="btn btn-success mt-3">Cadastrar Dono</button>
      </form>
    </div>
  );
}
