// src/pages/Evento/NovoEvento.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../../services/api';

const NovoEvento = () => {
  const { id } = useParams(); // id do pet
  const navigate = useNavigate();
  const [pet, setPet] = useState(null);
  const [tipo, setTipo] = useState('Hospedagem');
  const [observacoes, setObservacoes] = useState('');
  const [erro, setErro] = useState('');

  useEffect(() => {
    api.get(`/pets/${id}`)
      .then((res) => {
        setPet(res.data);
      })
      .catch((err) => {
        console.error('Erro ao buscar pet:', err);
        setErro('Não foi possível carregar os dados do pet.');
      });
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!pet || !pet._id) {
      setErro('Pet inválido.');
      return;
    }

    try {
      const novoEvento = {
        tipo,
        observacoes,
        pet: pet._id,
        dono: pet.dono?._id || pet.dono,
        tutor: pet.tutor?._id || pet.tutor,
        admin: pet.criador?._id || pet.criador,
        dataEntrada: new Date().toISOString(),
        status: 'Ativo'
      };

      const response = await api.post('/eventos', novoEvento);
      alert('Evento criado com sucesso!');
      navigate(`/pets/${id}`);
    } catch (error) {
      console.error('Erro ao criar evento:', error);
      setErro('Erro ao criar evento. Verifique os dados e tente novamente.');
    }
  };

  if (erro) return <div className="container mt-5 text-danger">{erro}</div>;

  if (!pet) return <div className="container mt-5">Carregando dados do pet...</div>;

  return (
    <div className="container mt-5">
      <h2>Novo Evento para: {pet.nome}</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Tipo de Evento:</label>
          <select className="form-control" value={tipo} onChange={(e) => setTipo(e.target.value)}>
            <option value="Hospedagem">Hospedagem</option>
            <option value="Passeio">Passeio</option>
            <option value="Banho">Banho</option>
            <option value="Consulta">Consulta</option>
            <option value="Alimentacao">Alimentação</option>
            <option value="Recreacao">Recreação</option>
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Observações:</label>
          <textarea className="form-control" value={observacoes} onChange={(e) => setObservacoes(e.target.value)} />
        </div>

        <button className="btn btn-success" type="submit">Salvar Evento</button>
      </form>
    </div>
  );
};

export default NovoEvento;
