// src/pages/Tutor/TutorHome.jsx
import React, { useEffect, useState } from 'react';
import { Container, Card, ListGroup, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';

// Formata ISO para dd/MM/yyyy, HH:mm:ss
const formatDateTime = iso =>
  new Date(iso).toLocaleString('pt-BR', {
    day   : '2-digit',
    month : '2-digit',
    year  : 'numeric',
    hour  : '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });

export default function TutorHome() {
  const navigate = useNavigate();
  const [pets, setPets] = useState([]);
  const [selectedPet, setSelectedPet] = useState(null);
  const [eventos, setEventos] = useState([]);

  // Ao montar, carrega tutor e seus pets
  useEffect(() => {
    const tutorJson = localStorage.getItem('usuario_tutor');
    if (!tutorJson) return navigate('/login-tutor');

    const tutor = JSON.parse(tutorJson);
    api.defaults.headers.common['Authorization'] = `Bearer ${tutor.token}`;

    // 1) Buscar pets desse tutor
    api.get(`/pets?tutor=${tutor._id}`)
      .then(({ data }) => {
        setPets(data);
        // pré-seleciona o primeiro
        if (data.length) setSelectedPet(data[0]._id);
      })
      .catch(() => { /* err */ });

    // 2) Buscar eventos do tutor
    api.get(`/eventos/tutor/${tutor._id}`)
      .then(({ data }) => setEventos(data))
      .catch(() => { /* err */ });
  }, [navigate]);

  // Quando o pet selecionado mudar, recarrega apenas eventos daquele pet
  useEffect(() => {
    if (!selectedPet) return;
    api.get(`/eventos/pet/${selectedPet}`)
      .then(({ data }) => setEventos(data))
      .catch(() => setEventos([]));
  }, [selectedPet]);

  const handleCheckIn = () => {
    if (!selectedPet) return;
    navigate(`/pets/${selectedPet}/novo-evento`);
  };

  const handleCheckOut = eventoId => {
    api.put(`/eventos/${eventoId}/checkout`, {
      dataSaida: new Date().toISOString()
    })
      .then(() => {
        // atualizar lista local
        setEventos(evts =>
          evts.map(evt =>
            evt._id === eventoId
              ? { ...evt, dataSaida: new Date().toISOString(), status: 'Finalizado' }
              : evt
          )
        );
      })
      .catch(() => alert('Falha ao executar check-out'));
  };

  return (
    <Container className="mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Painel do Tutor</h2>
        <Button variant="secondary" onClick={() => navigate('/')}>Sair</Button>
      </div>
      <div className="d-flex">
        {/* Lista de Pets */}
        <Card style={{ width: '220px' }} className="me-4">
          <Card.Header>Meus Pets</Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item
              action
              active={selectedPet === null}
              onClick={() => setSelectedPet(null)}
            >
              Todos Pets
            </ListGroup.Item>
            {pets.map(p => (
              <ListGroup.Item
                key={p._id}
                action
                active={p._id === selectedPet}
                onClick={() => setSelectedPet(p._id)}
              >
                {p.nome}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Card>

        {/* Eventos */}
        <Card className="flex-fill">
          <Card.Header className="d-flex justify-content-between align-items-center">
            <span>Meus Eventos</span>
            <Button variant="success" size="sm" onClick={handleCheckIn}>
              Check-in
            </Button>
          </Card.Header>
          <ListGroup variant="flush">
            {eventos.length === 0 && (
              <ListGroup.Item>Nenhum evento registrado.</ListGroup.Item>
            )}
            {eventos.map(evt => (
              <ListGroup.Item key={evt._id} className="d-flex justify-content-between">
                <div>
                  <strong>{evt.tipo}</strong> —<br />
                  Entrada: {formatDateTime(evt.dataEntrada)}<br />
                  Saída: {evt.dataSaida ? formatDateTime(evt.dataSaida) : '—'}<br />
                  Status: {evt.status}
                </div>
                {!evt.dataSaida && (
                  <Button
                    variant="warning"
                    size="sm"
                    onClick={() => handleCheckOut(evt._id)}
                  >
                    Check-out
                  </Button>
                )}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Card>
      </div>
    </Container>
  );
}
