// src/pages/PetDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

export default function PetDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [pet, setPet] = useState(null);

  useEffect(() => {
    api.get(`/pets/${id}`)
      .then(response => setPet(response.data))
      .catch(error => {
        console.error('Erro ao buscar pet:', error);
        navigate('/pets');
      });
  }, [id, navigate]);

  const handleDelete = async () => {
    if (window.confirm('Tem certeza que deseja excluir este pet?')) {
      try {
        await api.delete(`/pets/${id}`);
        navigate('/pets');
      } catch (error) {
        console.error('Erro ao excluir pet:', error);
      }
    }
  };

  if (!pet) return <div className="container mt-4">Carregando...</div>;

  return (
    <div className="container mt-4">
      <h2>Detalhes do Pet</h2>
      <p><strong>Nome:</strong> {pet.nome}</p>
      <p><strong>Raça:</strong> {pet.raca}</p>
      <p><strong>Porte:</strong> {pet.porte}</p>
      <p><strong>Sexo:</strong> {pet.sexo}</p>
      <p><strong>Cor:</strong> {pet.cor}</p>
      <p><strong>Castrado:</strong> {pet.castrado ? 'Sim' : 'Não'}</p>
      <p><strong>Carteira de Vacinação em Dia:</strong> {pet.vacinado ? 'Sim' : 'Não'}</p>

      <div className="d-flex gap-2 mt-3">
        <Link to={`/pets/${id}/novo-evento`} className="btn btn-success">Novo Evento</Link>
        <Link to={`/pets/${id}/editar`} className="btn btn-primary">Editar</Link>
        <button onClick={handleDelete} className="btn btn-danger">Excluir</button>
        <Link to="/pets" className="btn btn-secondary">Voltar à Lista</Link>
      </div>
    </div>
  );
}
