// src/pages/Pet/PetDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import api from '../../services/api';

export default function PetDetails() {
  const { id: petId } = useParams();
  const navigate = useNavigate();
  const [pet, setPet] = useState(null);
  const [eventos, setEventos] = useState([]);

  // Formata data ISO para dd/mm/yyyy
  const formatDate = (isoString) => {
    const d = new Date(isoString);
    return d.toLocaleDateString('pt-BR') + (isoString.includes('T') ? '' : '');
  };

  useEffect(() => {
    async function fetchData() {
      try {
        const [petRes, eventosRes] = await Promise.all([
          api.get(`/pets/${petId}`),
          api.get(`/eventos/pet/${petId}`)
        ]);
        setPet(petRes.data);
        setEventos(eventosRes.data);
      } catch (err) {
        console.error(err);
        navigate('/pets');
      }
    }
    fetchData();
  }, [petId, navigate]);

  const handleCheckout = async (eventoId) => {
    try {
      await api.put(`/eventos/${eventoId}/checkout`, {
        dataSaida: new Date().toISOString()
      });
      // recarrega lista de eventos
      const res = await api.get(`/eventos/pet/${petId}`);
      setEventos(res.data);
    } catch (err) {
      console.error(err);
      alert('Erro ao fazer check-out');
    }
  };

  if (!pet) return <p>Carregando...</p>;

  return (
    <div className="container mt-4">
      <div className="card mb-4">
        <div className="card-header">
          <h3>Detalhes do Pet</h3>
        </div>
        <div className="card-body">
          <p><strong>Nome:</strong> {pet.nome}</p>
          <p><strong>Espécie:</strong> {pet.especie}</p>
          <p><strong>Raça:</strong> {pet.raca}</p>
          <p><strong>Idade:</strong> {pet.idade} {pet.idade > 1 ? 'anos' : 'ano'}</p>
          <p><strong>Peso:</strong> {pet.peso} kg</p>
          <p><strong>Porte:</strong> {pet.porte}</p>
          <p><strong>Sexo:</strong> {pet.sexo}</p>
          <p><strong>Cor:</strong> {pet.cor}</p>
          <p><strong>Castrado:</strong> {pet.castrado ? 'Sim' : 'Não'}</p>
          <p><strong>Carteira de Vacinação em Dia:</strong> {pet.carteiraVacinacao ? 'Sim' : 'Não'}</p>
          <p><strong>Data de Cadastro:</strong> {formatDate(pet.dataCadastro)}</p>
          <p><strong>Observações:</strong> {pet.observacoes || '—'}</p>

          <Link to="/pets" className="btn btn-secondary me-2">Voltar ao Início</Link>
          <Link to={`/pets/${petId}/editar`} className="btn btn-warning me-2">Editar</Link>
          <Link to={`/pets/${petId}/novo-evento`} className="btn btn-success">Cadastrar Evento</Link>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h4>Eventos relacionados</h4>
        </div>
        <ul className="list-group list-group-flush">
          {eventos.length === 0 && (
            <li className="list-group-item">Nenhum evento registrado.</li>
          )}
          {eventos.map(evento => (
            <li key={evento._id} className="list-group-item">
              <div className="d-flex justify-content-between align-items-center">
                <div>
                  <strong>{evento.tipo}</strong>
                  {' – '}Check-in: {formatDate(evento.dataEntrada)}
                  {' – '}Check-out:{' '}
                  {evento.dataSaida ? formatDate(evento.dataSaida) : 'em andamento'}
                </div>
                {!evento.dataSaida && (
                  <button
                    className="btn btn-outline-success btn-sm"
                    onClick={() => handleCheckout(evento._id)}
                  >
                    Fazer Check-out
                  </button>
                )}
              </div>
              {/* Exibe observações logo abaixo */}
              {evento.observacoes && (
                <div className="mt-1">
                  <em>Observações:</em> {evento.observacoes}
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
