import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';
import { Container, Form, Button, Alert } from 'react-bootstrap';

const LoginAdmin = () => {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setErro('');

    try {
      const response = await api.get('/admins');
      const admins = response.data;

      const adminEncontrado = admins.find(
        (a) => a.email === email && a.senha === senha
      );

      if (adminEncontrado) {
        localStorage.setItem('usuario_admin', JSON.stringify(adminEncontrado));
        navigate('/painel-admin');
      } else {
        setErro('Email ou senha inválidos.');
      }
    } catch (err) {
      setErro('Erro ao conectar com o servidor.');
    }
  };

  return (
    <Container className="mt-5">
      <h2 className="text-center mb-4">Login Administrador</h2>
      <Form onSubmit={handleLogin} className="p-4 rounded shadow bg-white mx-auto" style={{ maxWidth: '400px' }}>
        {erro && <Alert variant="danger">{erro}</Alert>}
        <Form.Group className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Senha</Form.Label>
          <Form.Control
            type="password"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            required
          />
        </Form.Group>

        <div className="d-flex justify-content-between">
          <Button variant="primary" type="submit">
            Entrar
          </Button>
          <Button variant="secondary" onClick={() => navigate('/')}>Voltar</Button>
        </div>
      </Form>
    </Container>
  );
};

export default LoginAdmin;
