// src/pages/Cliente/ClienteHome.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';

export default function ClienteHome() {
  const navigate = useNavigate();
  const [dono, setDono] = useState(null);
  const [meusPets, setMeusPets] = useState([]);
  const [meusEventos, setMeusEventos] = useState([]);
  const [filtroPet, setFiltroPet] = useState(null);

  useEffect(() => {
    const usuarioStr = localStorage.getItem('usuario_cliente');
    if (!usuarioStr) {
      alert('VocÃª precisa estar logado como cliente.');
      navigate('/login-cliente');
      return;
    }

    const usuario = JSON.parse(usuarioStr);
    (async () => {
      try {
        // 1) Busca dados do dono
        const { data: donoData } = await api.get(`/donos/${usuario._id}`);
        setDono(donoData);

        // 2) Detalhes de cada pet
        const petObjs = await Promise.all(
          (donoData.pets || []).map(idPet =>
            api.get(`/pets/${idPet}`).then(r => r.data)
          )
        );
        setMeusPets(petObjs);

        // 3) Eventos de cada pet com petName
        const eventosPorPet = await Promise.all(
          petObjs.map(pet =>
            api.get(`/eventos/pet/${pet._id}`).then(res =>
              res.data.map(evt => ({ ...evt, petName: pet.nome }))
            )
          )
        );
        const allEventos = eventosPorPet
          .flat()
          .sort((a, b) => new Date(b.dataEntrada) - new Date(a.dataEntrada));
        setMeusEventos(allEventos);
      } catch (err) {
        console.error(err);
        alert('Erro ao carregar dados.');
        navigate('/login-cliente');
      }
    })();
  }, [navigate]);

  // Formata data/hora
  const formatDateTime = iso => new Date(iso).toLocaleString('pt-BR');

  if (!dono) return <p>Carregando perfil...</p>;

  const handleLogout = () => {
    localStorage.removeItem('usuario_cliente');
    navigate('/login-cliente');
  };

  // Eventos filtrados pelo pet selecionado
  const eventosExibidos = filtroPet
    ? meusEventos.filter(evt => evt.pet?.toString() === filtroPet)
    : meusEventos;

  return (
    <div className="container mt-4">
      {/* CabeÃ§alho */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Painel do Cliente</h2>
        <button className="btn btn-secondary" onClick={handleLogout}>Sair</button>
      </div>

      <div className="row">
        {/* Coluna esquerda: Dados e Pets */}
        <div className="col-md-4">
          {/* Meus Dados */}
          <div className="card mb-4">
            <div className="card-header bg-white border-0">
              <h5 className="mb-0">Meus Dados</h5>
            </div>
            <ul className="list-group list-group-flush">
              <li className="list-group-item"><strong>Nome:</strong> {dono.nome}</li>
              <li className="list-group-item"><strong>E-mail:</strong> {dono.email}</li>
              <li className="list-group-item"><strong>CPF:</strong> {dono.cpf}</li>
              <li className="list-group-item"><strong>Telefone:</strong> {dono.telefone}</li>
              <li className="list-group-item"><strong>EndereÃ§o:</strong> {dono.endereco}</li>
            </ul>
          </div>

          {/* Meus Pets com filtro */}
          <div className="card">
            <div className="card-header bg-white border-0">
              <h5 className="mb-0">Meus Pets</h5>
            </div>
            <ul className="list-group list-group-flush">
              {/* OpÃ§Ã£o para todos */}
              <li
                className={`list-group-item ${!filtroPet ? 'active' : ''}`}
                style={{ cursor: 'pointer' }}
                onClick={() => setFiltroPet(null)}
              >
                Todos Pets
              </li>
              {meusPets.map(pet => (
                <li
                  key={pet._id}
                  className={`list-group-item ${filtroPet === pet._id ? 'active' : ''}`}
                  style={{ cursor: 'pointer' }}
                  onClick={() => setFiltroPet(pet._id)}
                >
                  {pet.nome}
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* Coluna direita: Eventos filtrados */}
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-white border-0">
              <h5 className="mb-0">Meus Eventos</h5>
            </div>
            <ul className="list-group list-group-flush">
              {eventosExibidos.length === 0 ? (
                <li className="list-group-item">Nenhum evento registrado.</li>
              ) : (
                eventosExibidos.map(evt => (
                  <li key={evt._id} className="list-group-item mb-3">
                    <div className="d-flex justify-content-between">
                      <div>
                        <strong>{evt.tipo}</strong><br/>
                        <small><strong>Pet:</strong> {evt.petName}</small>
                      </div>
                      <div>
                        <strong>Entrada:</strong> {formatDateTime(evt.dataEntrada)}<br/>
                        <strong>SaÃ­da:</strong>{' '}
                        {evt.dataSaida ? formatDateTime(evt.dataSaida) : 'Em andamento'}<br/>
                        <strong>Status:</strong>{' '}
                        {evt.status || (evt.dataSaida ? 'ConcluÃ­do' : 'Ativo')}
                      </div>
                    </div>
                    {evt.observacoes && (
                      <div className="mt-2">
                        <em>ObservaÃ§Ãµes:</em> {evt.observacoes}
                      </div>
                    )}
                  </li>
                ))
              )}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
