// src/pages/DonoDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

export default function DonoDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [dono, setDono] = useState(null);
  const [editando, setEditando] = useState(false);
  const [form, setForm] = useState({
    nome: '',
    cpf: '',
    endereco: '',
    telefone: '',
    email: '',
    dataNascimento: '',
    responsavel: '',
    ativo: true,
    observacoes: ''
  });

  useEffect(() => {
    api.get(`/donos/${id}`).then((res) => {
      setDono(res.data);
      setForm(res.data);
    });
  }, [id]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    await api.put(`/donos/${id}`, form);
    setEditando(false);
    navigate('/donos');
  };

  const handleDelete = async () => {
    if (window.confirm('Tem certeza que deseja excluir este dono?')) {
      await api.delete(`/donos/${id}`);
      navigate('/donos');
    }
  };

  if (!dono) return <p>Carregando...</p>;

  return (
    <div className="container my-5">
      <h2>Detalhes do Dono</h2>
      <button className="btn btn-outline-secondary mb-3" onClick={() => navigate(-1)}>← Voltar</button>

      {editando ? (
        <form onSubmit={handleUpdate}>
          {['nome', 'cpf', 'endereco', 'telefone', 'email', 'responsavel'].map((field) => (
            <div key={field} className="mb-3">
              <label className="form-label text-capitalize">{field}</label>
              <input name={field} className="form-control" value={form[field]} onChange={handleChange} />
            </div>
          ))}

          <div className="mb-3">
            <label className="form-label">Data de Nascimento</label>
            <input type="date" name="dataNascimento" className="form-control" value={form.dataNascimento} onChange={handleChange} />
          </div>

          <div className="form-check mb-3">
            <input className="form-check-input" type="checkbox" name="ativo" checked={form.ativo} onChange={handleChange} />
            <label className="form-check-label">Ativo</label>
          </div>

          <div className="mb-3">
            <label className="form-label">Observações</label>
            <textarea name="observacoes" className="form-control" value={form.observacoes} onChange={handleChange} rows={3} />
          </div>

          <button type="submit" className="btn btn-primary me-2">Salvar</button>
          <button type="button" className="btn btn-secondary" onClick={() => setEditando(false)}>Cancelar</button>
        </form>
      ) : (
        <div>
          <p><strong>Nome:</strong> {dono.nome}</p>
          <p><strong>CPF:</strong> {dono.cpf}</p>
          <p><strong>Endereço:</strong> {dono.endereco}</p>
          <p><strong>Telefone:</strong> {dono.telefone || 'Não informado'}</p>
          <p><strong>Email:</strong> {dono.email || 'Não informado'}</p>
          <p><strong>Data de Nascimento:</strong> 
            {dono.dataNascimento
              ? new Date(dono.dataNascimento).toLocaleDateString('pt-BR')
              : 'Não informado'}
          </p>
          <p><strong>Responsável:</strong> {dono.responsavel ? 'Sim ✅' : 'Não ❌'}</p>
          <p><strong>Ativo:</strong> {dono.ativo ? 'Sim ✅' : 'Não ❌'}</p>
          <p><strong>Observações:</strong> 
            {dono.observacoes && dono.observacoes.trim() !== ''
              ? dono.observacoes
              : 'Nenhuma observação registrada.'}
          </p>

          <button className="btn btn-warning me-2" onClick={() => setEditando(true)}>Editar</button>
          <button className="btn btn-danger" onClick={handleDelete}>Excluir</button>
        </div>
      )}
    </div>
  );
}
