// src/pages/PetDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';

export default function PetDetails() {
  const { id } = useParams();
  const [pet, setPet] = useState(null);
  const [eventos, setEventos] = useState([]);
  const [erro, setErro] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const [petRes, eventosRes] = await Promise.all([
          api.get(`/pets/${id}`),
          api.get(`/eventos/pet/${id}`)  // üîÅ ROTA CORRIGIDA AQUI
        ]);
        setPet(petRes.data);
        setEventos(eventosRes.data);
      } catch (err) {
        console.error(err);
        setErro('Erro ao carregar dados do pet ou eventos.');
      }
    }
    fetchData();
  }, [id]);

  const handleCheckout = async (eventoId) => {
    try {
      const now = new Date().toISOString();
      const res = await api.put(`/eventos/${eventoId}/checkout`, {
        dataSaida: now,
        observacoes: `Check-out realizado em ${new Date(now).toLocaleString()}`
      });
      setEventos(evts =>
        evts.map(e => (e._id === eventoId ? res.data : e))
      );
    } catch (err) {
      console.error(err);
      setErro('Erro ao finalizar evento');
    }
  };

  if (!pet) return <div className="container my-5">Carregando pet‚Ä¶</div>;

  return (
    <div className="container my-5">
      <h2>{pet.nome} ‚Äì {pet.raca}</h2>
      <p><strong>Esp√©cie:</strong> {pet.especie}</p>
      <p><strong>Porte:</strong> {pet.porte}</p>
      <p><strong>Tutor:</strong> {pet.tutor?.nome}</p>
      <p><strong>Dono respons√°vel:</strong> {pet.dono?.nome}</p>
      <hr />

      {erro && <div className="alert alert-danger">{erro}</div>}

      <div className="d-flex justify-content-between align-items-center">
        <h3>Hist√≥rico de Eventos</h3>
        <Link to={`/pets/${id}/novo-evento`} className="btn btn-success">
          + Novo Evento
        </Link>
      </div>

      <ul className="list-group mt-3">
        {eventos.map(evt => {
          const entrada = new Date(evt.dataEntrada).toLocaleString();
          const saida = evt.dataSaida && new Date(evt.dataSaida).toLocaleString();
          return (
            <li key={evt._id} className="list-group-item d-flex justify-content-between align-items-start">
              <div>
                <strong>{evt.tipo.toUpperCase()}</strong> ‚Äî {entrada}
                {saida && <> at√© {saida}</>}
                <br />
                <small>{evt.observacoes}</small>
              </div>
              {!saida && (
                <button
                  className="btn btn-outline-primary btn-sm"
                  onClick={() => handleCheckout(evt._id)}
                >
                  Check-out
                </button>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
}
