import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

const PetDetails = () => {
  const { id } = useParams();
  const [pet, setPet] = useState(null);
  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    // Buscar dados do pet
    axios.get(`/api/pets/${id}`)
      .then(res => setPet(res.data))
      .catch(err => console.error(err));

    // Buscar eventos desse pet
    axios.get(`/api/eventos/pet/${id}`)
      .then(res => setEventos(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!pet) return <div className="container my-5">Carregando pet…</div>;

  return (
    <div className="container my-5">
      <h2>{pet.nome} – {pet.raca}</h2>
      <p><strong>Espécie:</strong> {pet.especie}</p>
      <p><strong>Porte:</strong> {pet.porte}</p>
      <p><strong>Tutor:</strong> {pet.tutor}</p>
      <p><strong>Dono responsável:</strong> {pet.dono}</p>

      <hr />

      <div className="d-flex justify-content-between align-items-center">
        <h3>Histórico de Eventos</h3>
        <Link to={`/pets/${id}/novo-evento`} className="btn btn-success">
          + Novo Evento
        </Link>
      </div>

      <ul className="list-group mt-3">
        {eventos.map(evt => (
          <li key={evt._id} className="list-group-item">
            <strong>{evt.tipo.toUpperCase()}</strong> 
            {` — ${new Date(evt.dataEntrada).toLocaleString()}`}
            {evt.status === 'finalizado' && evt.dataSaida &&
              ` até ${new Date(evt.dataSaida).toLocaleString()}`}
            <br/>
            <small>{evt.observacoes}</small>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PetDetails;
