import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../../services/api';

export default function DonoDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [dono, setDono] = useState(null);

  useEffect(() => {
    api.get(`/donos/${id}`).then((res) => {
      setDono(res.data);
    });
  }, [id]);

  const handleDelete = async () => {
    if (window.confirm('Tem certeza que deseja excluir este dono?')) {
      await api.delete(`/donos/${id}`);
      navigate('/donos');
    }
  };

  if (!dono) return <p>Carregando...</p>;

  return (
    <div className="container my-5">
      <button className="btn btn-secondary mb-3" onClick={() => navigate(-1)}>← Voltar à Lista</button>
      <h2>Detalhes do Dono</h2>

      <ul className="list-group mb-3">
        <li className="list-group-item"><strong>Nome:</strong> {dono.nome}</li>
        <li className="list-group-item"><strong>CPF:</strong> {dono.cpf}</li>
        <li className="list-group-item"><strong>Endereço:</strong> {dono.endereco}</li>
        <li className="list-group-item"><strong>Telefone:</strong> {dono.telefone || 'Não informado'}</li>
        <li className="list-group-item"><strong>Email:</strong> {dono.email || 'Não informado'}</li>
        <li className="list-group-item"><strong>Data de Nascimento:</strong> {dono.dataNascimento ? new Date(dono.dataNascimento).toLocaleDateString('pt-BR') : 'Não informado'}</li>
        <li className="list-group-item"><strong>Responsável:</strong> {dono.responsavel ? 'Sim ✅' : 'Não ❌'}</li>
        <li className="list-group-item"><strong>Ativo:</strong> {dono.ativo ? 'Sim ✅' : 'Não ❌'}</li>
        <li className="list-group-item"><strong>Observações:</strong> {dono.observacoes?.trim() || 'Nenhuma observação registrada.'}</li>
      </ul>

      <div className="mb-4">
        <Link to={`/donos/${id}/editar`} className="btn btn-warning me-2">Editar</Link>
        <button className="btn btn-danger" onClick={handleDelete}>Excluir</button>
      </div>
    </div>
  );
}
